arguments.argument:
xoffset = view_xview[0]
yoffset = view_yview[0]
xsize = view_wview[0]
ysize = view_hview[0]
xshift = -320*global.timerPos
yshift = 5*global.timerPos
drawx = xoffset+xsize/2
redScale = 2
blueScale = 2
draw_set_alpha(1)

draw_sprite_ext(sprite_index, 0,drawx, yoffset+ysize-36,3,3,0,c_white,1)

draw_set_halign(fa_center)
draw_set_valign(fa_center)
draw_set_color(c_black)
if global.myself!=-1{
    if instance_exists(global.myself){
        if global.myself.MGE_currentArena!=-1{
            if redScore[global.myself.MGE_currentArena]>=10{
                redScale = 1
            }else{
                redScale = 2
            }
            if blueScore[global.myself.MGE_currentArena]>=10{
                blueScale = 1
            }else{
                blueScale = 2
            }
            draw_text_transformed(drawx-42,yoffset+ysize-35,redScore[global.myself.MGE_currentArena],redScale,redScale,0)
            draw_text_transformed(drawx+45,yoffset+ysize-35,blueScore[global.myself.MGE_currentArena],blueScale,blueScale,0)
        }else{
            draw_text_transformed(drawx-42,yoffset+ysize-35,"0",2,2,0)
            draw_text_transformed(drawx+45,yoffset+ysize-35,"0",2,2,0)
        }
    }

    draw_set_color(c_white)
    if global.myself.team==TEAM_SPECTATOR and global.isLive==1{
        //draw_text_transformed((xoffset+xsize/2),(yoffset+ysize/2)+55+33,"Type /arena <1-5> to join.",1,1,0)
    }
}

draw_set_color(c_white)

draw_roundtimer(xoffset, yoffset, xsize, ysize, timer)
