arguments.argument:
if !done and global.myself.team!=team{
    ClientPlayerChangeteam(team,global.serverSocket)
    socket_send(global.serverSocket)
}

if team==TEAM_SPECTATOR{
    if instance_exists(Spectator){
        with(Spectator){
            instance_destroy()
        }
    }
    instance_create(map_width()/2,map_height()/2,Spectator)
    global.startedGame=0
}

if global.startedGame==1{
    alarm[1]=5
}else{
    done=true
}
