arguments.argument:
//Checks for any input
if keyboard_string != ""{
    current_text = string_copy(string_copy(current_text, 0, cursor_position) + keyboard_string + string_copy(current_text, cursor_position+1, string_length(current_text)),0,CHAT_MAX_STRING_LENGTH-1)
    cursor_position += string_length(keyboard_string);
    keyboard_string = "";
}
