arguments.argument:
if global.writeChatLog==0 and global.commandLogged==0 exit;

var chatLogFile,datetime;
datetime=string(date_datetime_string(date_current_datetime())){
    datetime=string_replace_all(datetime,"<","_");datetime=string_replace_all(datetime,">","_");
    datetime=string_replace_all(datetime,":","_");datetime=string_replace_all(datetime,'"',"_");
    datetime=string_replace_all(datetime,"/","_");datetime=string_replace_all(datetime,"\","_");
    datetime=string_replace_all(datetime,"|","_");datetime=string_replace_all(datetime,"?","_");
    datetime=string_replace_all(datetime,"*","_")
}
chatLogFile=file_text_open_write(working_directory+"\Logs\ChatLog__"+datetime+".txt")

for(i=0; i<ds_list_size(chatLog); i+=1){
    file_text_write_string(chatLogFile,ds_list_find_value(chatLog,i))
    file_text_writeln(chatLogFile)
}

file_text_close(chatLogFile)

console_print('Chat log saved as: ChatLog__'+datetime+'.txt')
