arguments.argument:
if global.writeConsoleLog==0 and global.commandLogged==0 exit;

var consoleLogFile,datetime;
datetime=string(date_datetime_string(date_current_datetime())){
    datetime=string_replace_all(datetime,"<","_");datetime=string_replace_all(datetime,">","_");
    datetime=string_replace_all(datetime,":","_");datetime=string_replace_all(datetime,'"',"_");
    datetime=string_replace_all(datetime,"/","_");datetime=string_replace_all(datetime,"\","_");
    datetime=string_replace_all(datetime,"|","_");datetime=string_replace_all(datetime,"?","_");
    datetime=string_replace_all(datetime,"*","_")
}
consoleLogFile=file_text_open_write(working_directory+"\Logs\ConsoleLog__"+datetime+".txt")

for(i=0; i<ds_list_size(global.commandLog); i+=1){
    file_text_write_string(consoleLogFile,ds_list_find_value(global.commandLog,i))
    file_text_writeln(consoleLogFile)
}

file_text_close(consoleLogFile)

console_print('Console log saved as: ConsoleLog__'+datetime+'.txt')
