arguments.argument:
//Picking captains
global.pugMode=1

//Move all players to spectator
for (i=0; i<ds_list_size(global.players); i+=1){
    player=ds_list_find_value(global.players, i)
    player.team=TEAM_SPECTATOR

    if player.object!=-1{
        with(player.object){
            if (!instance_exists(lastDamageDealer) || lastDamageDealer == player){
                sendEventPlayerDeath(player, player, noone, DAMAGE_SOURCE_BID_FAREWELL);
                doEventPlayerDeath(player, player, noone, DAMAGE_SOURCE_BID_FAREWELL);
            }else{
                var assistant;
                assistant = secondToLastDamageDealer;
                if (lastDamageDealer.object){
                    if (lastDamageDealer.object.healer){
                        assistant = lastDamageDealer.object.healer;
                    }
                }
                sendEventPlayerDeath(player, lastDamageDealer, assistant, DAMAGE_SOURCE_FINISHED_OFF);
                doEventPlayerDeath(player, lastDamageDealer, assistant, DAMAGE_SOURCE_FINISHED_OFF);
            }
        }
    }
    ServerPlayerChangeteam(ds_list_find_index(global.players,player),player.team,global.sendBuffer)
}

if pug==1{
    chat_sendmsg(global.chatPrintPrefix+C_PINK+"PUG "+C_WHITE+"started! Type "+C_GREEN+"/captain"+C_WHITE+" to become "+P_RED+"RED team's"+C_WHITE+" captain.")
    stage=1 //red captain
}else if mix==1{
    chat_sendmsg(global.chatPrintPrefix+C_PINK+"MIX "+C_WHITE+"started! Team"+C_GREEN+" map "+C_WHITE+"picking will now begin!")
    alarm[2]=60/global.delta_factor

    event_user(4)
}else if war==1{
    chat_sendmsg(global.chatPrintPrefix+C_PINK+"WAR "+C_WHITE+"started! Type "+C_GREEN+"/captain"+C_WHITE+" to become "+P_RED+"RED team's"+C_WHITE+" captain.")
    stage=1 //red captain
}
