arguments.argument:
if war==1{
    var message;
    message = global.chatPrintPrefix+C_WHITE+"Join your teams! Captains confirm or unconfirm the final teams with "+C_GREEN+"/captain"+C_WHITE+"."
    write_ubyte(global.publicChatBuffer, CHAT_PUBLIC_MESSAGE)
    write_ushort(global.publicChatBuffer, string_length(message))
    write_string(global.publicChatBuffer, message)
    write_byte(global.publicChatBuffer,-1)
    print_to_chat(message);// For the host
    break;
}

//Skip the picking stage when irrelevant
if maxPicks<=2{
    stage=4
    alarm[2]=30/global.delta_factor
    exit;
}

//Start picking teams
var message;
message = global.chatPrintPrefix+P_RED+"RED"+C_WHITE+" captain picks first! Type"+C_GREEN+" /pick "+C_PINK+"<number>"
write_ubyte(global.publicChatBuffer, CHAT_PUBLIC_MESSAGE)
write_ushort(global.publicChatBuffer, string_length(message))
write_string(global.publicChatBuffer, message)
write_byte(global.publicChatBuffer,-1)
print_to_chat(message);// For the host

event_user(0)
