arguments.argument:
if war==1{
    chat_sendmsg(global.chatPrintPrefix+C_WHITE+"Join your teams! Captains confirm or unconfirm the final teams with "+C_GREEN+"/captain"+C_WHITE+".")
    break;
}

//Skip the picking stage when irrelevant
if maxPicks<=2{
    stage=4
    alarm[2]=30/global.delta_factor
    exit;
}

//Start picking teams
chat_sendmsg(global.chatPrintPrefix+P_RED+"RED"+C_WHITE+" captain picks first! Type"+C_GREEN+" /pick "+C_PINK+"<number>")

event_user(0)
