arguments.argument:
//Create a list for picking players
pickingList=""
ds_list_clear(pickingPlayers)

for (i=0; i<ds_list_size(global.players); i+=1){
    player=ds_list_find_value(global.players, i)

    if player.team==TEAM_SPECTATOR{
        ds_list_add(pickingPlayers,player)
        pickingList+=C_GREEN+string(ds_list_size(pickingPlayers))+". "+C_WHITE+sanitiseNewlines(player.name)+" "
    }
}

var colour,teamString;
if teamToPick==0{
    colour=P_RED
    teamString="RED"
}
if teamToPick==1{
    colour=P_BLUE
    teamString="BLUE"
}

chat_sendmsg(global.chatPrintPrefix+colour+teamString+"'s pick: "+ pickingList)
