arguments.argument:
//Announce scores
var drawString;
drawString=""
if drawScore!=0{
    drawString=C_WHITE+" : "+C_GREEN+"DRAW - "+string(drawScore)
}
chat_sendmsg(global.chatPrintPrefix+C_GREEN+"Scores: "+P_RED+"RED - "+string(redScore)+C_WHITE+" : "+P_BLUE+"BLUE - "+string(blueScore)+drawString)

if gamesPlayed==5{
    var endString;
    endString=""

    if redScore>blueScore{
        endString=P_RED+"RED"+C_WHITE+" wins "+P_RED+string(redScore)+C_WHITE+":"+P_BLUE+string(blueScore)
    }else if redScore<blueScore{
        endString=P_BLUE+"BLUE"+C_WHITE+" wins "+P_BLUE+string(blueScore)+C_WHITE+":"+P_RED+string(redScore)
    }else if redScore==blueScore{

    }

    chat_sendmsg(global.chatPrintPrefix+endString)

    instance_destroy()
}
