arguments.argument:
//Announce scores
var drawString;
drawString=""
if drawScore!=0{
    drawString=C_WHITE+" : "+C_GREEN+"DRAW - "+string(drawScore)
}
var message;
message = global.chatPrintPrefix+C_GREEN+"Scores: "+P_RED+"RED - "+string(redScore)+C_WHITE+" : "+P_BLUE+"BLUE - "+string(blueScore)+drawString
write_ubyte(global.publicChatBuffer, CHAT_PUBLIC_MESSAGE)
write_ushort(global.publicChatBuffer, string_length(message))
write_string(global.publicChatBuffer, message)
write_byte(global.publicChatBuffer,-1)
print_to_chat(message);// For the host

if gamesPlayed==5{
    var endString;
    endString=""

    if redScore>blueScore{
        endString=P_RED+"RED"+C_WHITE+" wins "+P_RED+string(redScore)+C_WHITE+":"+P_BLUE+string(blueScore)
    }else if redScore<blueScore{
        endString=P_BLUE+"BLUE"+C_WHITE+" wins "+P_BLUE+string(blueScore)+C_WHITE+":"+P_RED+string(redScore)
    }else if redScore==blueScore{

    }

    var message;
    message = global.chatPrintPrefix+endString
    write_ubyte(global.publicChatBuffer, CHAT_PUBLIC_MESSAGE)
    write_ushort(global.publicChatBuffer, string_length(message))
    write_string(global.publicChatBuffer, message)
    write_byte(global.publicChatBuffer,-1)
    print_to_chat(message);// For the host

    instance_destroy()
}
