arguments.argument:
{
    menu_create(48, 110, 500, 300, 32, 40, 104, 6)
    menumode = true;
    virnum=0

    if (room != ModOptions){
        menu_setdimmed();
    }

    menu_background(512, 24, 8, 12, 4);
    bgtabs = true;

    //we dont want to freeze the server with game maker message boxes
    if room == ModOptions{
        menu_addlink("New Config", '
            config_new()
        ');
    }

    if room == ModOptions{
        menu_addedit_text("Load Config", "global.currentConfig", '
            config_load(argument0)
        ')
    }

    if room != ModOptions and global.isHost==1{
        menu_addedit_text("Load Config", "global.currentConfig", '
            config_load(argument0)
        ')
    }

    //menu_addlink("Load Config", '
    //');
    //menu_addlink("Edit Config", '
    //');


    //TABS
    menu_addtablink("Hosting", '
        instance_destroy();
        instance_create(x,y,ModHostOptionsController);
    ');

    menu_addtablink("Chat", '
        instance_destroy();
        instance_create(x,y,ModChatOptionsController);
    ');

    menu_addtablink("Classlimits", '
        instance_destroy();
        instance_create(x,y,ModClasslimitsController);
    ');

    menu_addtablink("Configs", '
        instance_destroy();
        instance_create(x,y,ModConfigsController);
    ');

    menu_addtablink("Bots", '
        instance_destroy();
        instance_create(x,y,ModBotOptionsController);
    ');

    menu_addback("[0] Back", '
        instance_destroy()
        instance_create(0,0,ModOptionsParent)
    ');
}
