arguments.argument:
{
    menu_create(48, 110, 500, 300, 32, 40, 104, 6)
    menumode = true;
    virnum=0
    setVirNum=0

    if (room != ModOptions){
        menu_setdimmed();
    }

    menu_background(512, 24, 8, 12, 4);
    bgtabs = true;

    menu_addedit_boolean("Dumbledank's Sprite Pack:","global.dumbledankSprites",'
        es_write_ini("s","DumbledankSprites",argument0)
    ')setVirNum+=1

    menu_addedit_boolean("Show Weapon Only When Shooting:","global.weaponDraw",'
        es_write_ini("s","WeaponDraw",argument0)
    ')setVirNum+=1

    menu_addedit_boolean("Yellow Mine Trails:","global.yellowMineTrails",'
        es_write_ini("s","YellowMineTrails",argument0)
    ')setVirNum+=1

    menu_addedit_boolean("Hide Gun Shells:","global.noShells",'
        es_write_ini("s","NoShells",argument0)
    ')setVirNum+=1

    menu_addedit_num("Corpse Fade Delay:","global.fastBodyFade",'
        es_write_ini("s","FastBodyFade",argument0)
    ')setVirNum+=1

    menu_addedit_boolean("Cave Story Death Animations:","global.caveStoryDeath",'
        es_write_ini("s","CaveStoryDeath",argument0)
    ')setVirNum+=1

    //TABS
    menu_addtablink("HUDs", '
        instance_destroy();
        instance_create(x,y,ModOptGFXBasic);
    ');

    menu_addtablink("HUDs Adv.", '
        instance_destroy();
        instance_create(x,y,ModOptGFXAdv);
    ');

    menu_addtablink("Graphics", '
        instance_destroy();
        instance_create(x,y,ModOptGFXGraphics);
    ');

    menu_addback("[0] Back", '
        instance_destroy()
        instance_create(0,0,ModOptionsParent)
    ');
}
