    exit;
}

//Stop going off the top
if y>-2{
    vspeed*=-1

}

//Stop left
if x>-2{
    hspeed*=-1

}

//Stop bottom
if y+(sprite_get_height(sprite_index)*scaleFactor)<room_height+2{
    vspeed*=-1
}

//Stop right
if x+(sprite_get_width(sprite_index)*scaleFactor)<room_width+2{
    hspeed*=-1
}


if starty>-2{
    event_user(0)
}

if startx>-2{
    event_user(0)
}

