    menu_addedit_key("Spy Bubble:","global.bubbleSpy")setVirNum+=1
    menu_addedit_key("Global Chat:","global.chatGlobalCtrl")setVirNum+=1
    menu_addedit_key("Team Chat:","global.chatTeamCtrl")setVirNum+=1
    menu_addedit_key("Toggle Chat:","global.chatToggleCtrl")setVirNum+=1
    menu_addedit_key("Hide Chat:","global.chatHideCtrl")setVirNum+=1
    menu_addedit_key("Open/Close Console:","global.consoleKey")setVirNum+=1

    //TABS
    menu_addtablink("Host", '
        instance_destroy()
        instance_create(x,y,ModHostOptionsController)
    ');
    menu_addtablink("C-Limits", '
        instance_destroy()
        instance_create(x,y,ClasslimitsMenuController)
    ');
    menu_addtablink("Bots", '
        instance_destroy()
        instance_create(x,y,ModBotOptionsController)
    ');
    menu_addtablink("GFX Basic", '
        instance_destroy()
        instance_create(x,y,ModOptGFXBasic)
    ');
    menu_addtablink("GFX Adv", '
        instance_destroy()
        instance_create(x,y,ModOptGFXAdv)
    ');
    menu_addtablink("Misc.", '
        instance_destroy()
        instance_create(x,y,ModOptMisc)
    ');
    menu_addtablink("Controls", '
        instance_destroy()
        instance_create(x,y,ModControlsOptionsController)
    ');
    menu_addtablink("Configs", '
        instance_destroy()
        instance_create(x,y,ModConfigsController)
    ');

    menu_addback("Back", '
        instance_destroy()
        if(room == ModOptions){
            room_goto_fix(Menu)
        }else{
            instance_create(0,0,InGameMenuController)
        }
    ');
