arguments.argument:
if global.displayingPing==1{
    docol=c_red
    showping=string(ping)
    if (ping<275){
        docol=c_yellow
    }
    if (ping<135){
        docol=c_green
    }
    if (ping<75){
        docol=c_aqua
    }
    if (ping==-1){
        docol=c_gray
        showping='TIMEOUT'
    }
    draw_set_valign(fa_center)
    draw_set_halign(fa_middle)
    draw_set_color(c_black)
    draw_set_alpha(0.7)
    draw_rectangle(view_xview+140,view_yview+520,view_xview+180,view_yview+550,false)

    draw_set_color(docol)
    draw_set_alpha(1)
    draw_text(view_xview[0]+160, view_yview[0]+535,showping)
}
