arguments.argument:
{
    cursor_sprite = CrosshairS;
    global.levelchoice = 1

    menu_create(40, 100, 200, 200, 32);
    menu_background(212, 24, 8, 12, 4);
    menu_setdimmed();

    menu_addlink("Return to Game", '
        instance_destroy();
    ');
    menu_addback("", '
        instance_destroy();
    ');

    menu_addlink("Console", '
        instance_destroy();
        if !(instance_exists(Console))
            instance_create(0, 0, Console);
    ');
    if (global.isHost) {
        menu_addlink("Kick players", '
            if(!instance_exists(ScoreTableController)) instance_create(0,0,ScoreTableController);
            ScoreTableController.showadmin = true;
            instance_destroy();
        ');
    }
    menu_addlink("Options", '
        instance_destroy();
        instance_create(0,0,OptionsController);
    ');
    menu_addlink("DSM Options", '
        instance_destroy();
        instance_create(0,0,DSMOptions_General);
    ');

    menu_divider("");

    menu_addlink("Lobby", '
        instance_destroy();
        instance_create(112, 224, LobbyController);
        instance_create(176, 160, LobbySideController);
        instance_create(176, 80, LobbyNameController);
        instance_create(564, 152, ScrollUpO);
        instance_create(564, 528, ScrollDownO);
    ');
    menu_addlink("Disconnect", '
        // Force dedicated mode to off so you can go to main menu instead of just restarting server
        if (show_question("Do you really want to leave this match?")) {
            if (global.serverPluginsInUse)
            {
                pluginscleanup(true);
            }
            else
            {
                global.dedicatedMode = 0;
                with(Client)
                    instance_destroy();

                with(GameServer)
                    instance_destroy();
            }
        }
    ');
    menu_addlink("Quit Game", '
        if (show_question("Do you really want to quit?")) {
            console_save_log();
            game_end();
        }
    ');
}
