arguments.argument:
{
    draw_background(menuimage, 0, 0);

    if global.dsmBackground==0{
    // display background info, if downloaded
    if (global.backgroundHash != "default")
    {
        draw_set_halign(fa_right);
        draw_set_valign(fa_bottom);

        // hovering over title
        if (mouse_x >= 595 - string_width(global.backgroundTitle)
            && mouse_x < 595
            && mouse_y >= 595 - string_height(global.backgroundTitle)
            && mouse_y < 595
            && global.backgroundURL != "")
        {
            draw_set_color(c_orange);
        }
        else
        {
            draw_set_color(c_white);
        }
        draw_text(595, 595, global.backgroundTitle);
    }

    // fade out old background
    if (fade > 0)
    {
        draw_background_ext(oldBackground, 0, 0, 1, 1, 0, c_white, fade);
    }

    }

    if (!(global.backgroundHash != "default" && !global.backgroundShowVersion) and (global.dsmBackground==0 or global.dsmBackground==3))
    {
        draw_sprite(MenuBackgroundVersionS,0,300,300);
    }

    if global.dsmBackground==1{
        if global.textHighlightColour==0{
            draw_sprite(DSM_MenuBackgroundVersion2S,0,300,300);
        }else if global.textHighlightColour==1{
           draw_sprite(DSM_MenuBackgroundVersion3S,0,300,300);
        }else if global.textHighlightColour==2{
            draw_sprite(DSM_MenuBackgroundVersion1S,0,300,300);
        }else if global.textHighlightColour==3{
            draw_sprite(versionImage,0,300,300);
        }
    }else if global.dsmBackground==2{
        draw_sprite(versionImage,0,300,300);
    }
}
