arguments.argument:
{
    var i, val; 

    if dimmed {   
        draw_set_color(c_black);
        draw_set_alpha(0.8);
        draw_rectangle(view_xview[0], view_yview[0], view_xview[0]+view_wview[0], view_yview[0]+view_hview[0], false);   
    }

    draw_set_color(c_white);

    draw_set_valign(fa_middle);
    draw_set_halign(fa_left);
    draw_set_alpha(1);

    for(i=0; i<items; i+=1) {
        if editing == i {
            if global.textHighlightColour==0{
                draw_set_color(c_orange);
            }else if global.textHighlightColour==1{
                draw_set_color(c_aqua);
            }else if global.textHighlightColour==2{
                draw_set_color(c_lime);
            }else if global.textHighlightColour==3{
                draw_set_color(colour2)
            }
        }
        else if(i == virtualitem) {
            if global.textHighlightColour==0{
                draw_set_color(c_red);
            }else if global.textHighlightColour==1{
                draw_set_color(c_blue);
            }else if global.textHighlightColour==2{
                draw_set_color(c_green);
            }else if global.textHighlightColour==3{
                draw_set_color(colour1);
            } 
        } else {
            draw_set_color(c_white);    
        }

        draw_text(view_xview[0]+xbegin,view_yview[0]+ybegin+i*spacing,item_name[i]);
        switch (item_type[i]) {
            case "script":
                val = "";
                break;
            case "text":
                val = argument1;
                break;
            case "edittext2":
            case "edittext":
            case "editnum":
                val = item_value[i];
                break;
            case "editselect":
                val = item_option_label[i, item_value[i]];
                break;
            case "editkey":
                if editing == i {
                    val = "Please press a key";
                    break;
                }
            case "editkeyormouse":
                if editing == i {
                    val = "Please press a key or mouse button";
                    break;
                }
                switch (menu_get_var(i)) {
                    case vk_alt:
                        val = "Alt";
                        break;
                    case vk_control:
                        val = "Control";
                        break;
                    case vk_down:
                        val = "Down";
                        break;
                    case vk_up:
                        val = "Up";
                        break;
                    case vk_left:
                        val = "Left";
                        break;
                    case vk_right:
                        val = "Right";
                        break;
                    case vk_enter:
                        val = "Enter";
                        break;
                    case vk_tab:
                        val = "Tab";
                        break;
                    case vk_space:
                        val = "Space Bar";
                        break;
                    case vk_shift:
                        val = "Shift";
                        break;
                    case vk_f1:
                        val = "F1"
                        break;
                    case vk_f2:
                        val = "F2"
                        break;
                    case vk_f3:
                        val = "F3"
                        break;
                    case vk_f4:
                        val = "F4"
                        break;
                    case vk_f5:
                        val = "F5"
                        break;
                    case vk_f6:
                        val = "F6"
                        break;
                    case vk_f7:
                        val = "F7"
                        break;
                    case vk_f8:
                        val = "F8"
                        break;
                    case vk_f9:
                        val = "F9"
                        break;
                    case vk_f10:
                        val = "F10"
                        break;
                    case vk_f11:
                        val = "F11"
                        break;
                    case vk_f12:
                        val = "F12"
                        break;
                    case MOUSE_LEFT:
                        val = "Left Mouse Button";
                        break;
                    case MOUSE_RIGHT:
                        val = "Right Mouse Button";
                        break;
                    default:
                        val = chr(menu_get_var(i));
                        break;
                }
                break;
        }
        draw_text(view_xview[0]+xbegin+valueoffset,view_yview[0]+ybegin+i*spacing,val);
    }
}
