arguments.argument:
if timer > 0{
    timer -=1 * global.delta_factor;
    if global.redCaps == global.caplimit{
        global.winners = TEAM_RED;
    }else if global.blueCaps == global.caplimit{
        global.winners = TEAM_BLUE;
    }
}else if timer == 0{
    if instance_exists(IntelligenceRed) && instance_exists(IntelligenceBlue){
        if (IntelligenceRed.x == IntelligenceBaseRed.x
        and IntelligenceRed.y == IntelligenceBaseRed.y
        and IntelligenceBlue.x == IntelligenceBaseBlue.x
        and IntelligenceBlue.y == IntelligenceBaseBlue.y){
            if global.redCaps > global.blueCaps{
                global.winners = TEAM_RED;
            }else if global.blueCaps > global.redCaps{
                global.winners = TEAM_BLUE;
            }else{
                global.winners = TEAM_SPECTATOR;
            }
        }else{
            overtime = 1;
        }
    }else{
        overtime = 1;
    }
}
