arguments.argument:
if (timer > 0){
    timer -= 1 * global.delta_factor;
}

if (mode == 1 and global.run_virtual_ticks) and global.isLive==1{
    if (global.setupTimer > 0){
        global.setupTimer -= 1;
    }
    if (global.setupTimer == 90 or global.setupTimer == 120 or global.setupTimer == 150 or global.setupTimer == 180){
        sound_play(CountDown1Snd);
    }else if (global.setupTimer == 60){
        sound_play(CountDown2Snd);
    }else if (global.setupTimer == 30){
        timer = max(2*30*60,timeLimit)
        sound_play(SirenSnd);
        with (Medigun){
            ubering=false
            uberCharge=2000
            uberReady=true
        }
    }
}


//overtime?
overtime = 0;
if (timer <= 0) and mode==0{
    for (i=1; i<= global.totalControlPoints; i+=1){
        if (global.cp[i].capping > 0){
            overtime = 1;
        }
    }
}

//check for winner
//mode 0 traditional, mode 1 attack/defend
if (mode == 0){
    if (global.cp[1].team == global.cp[global.totalControlPoints].team){
        if (global.cp[1].team == TEAM_RED){
            global.winners = TEAM_RED;
        }else if (global.cp[1].team == TEAM_BLUE){
            global.winners = TEAM_BLUE;
        }
    }else if (timer <= 0 and overtime == 0){
        global.winners = TEAM_SPECTATOR;
    }
}else if (mode == 1){
    if (global.cp[global.totalControlPoints].team == TEAM_RED){
        global.winners = TEAM_RED;
    }else if (timer <= 0 and overtime == 0){
        global.winners = TEAM_BLUE;
    }
}

if overtime==1 and mode==0{
//if team controls more than global.totalControlPoints/2 cp, they win
//otherwise if team has even number of cps, overtime until a cap
}

//Assign a team
if global.currentStopwatchRound!=1 exit;
if global.adcpStopwatch==1 and mode==1 and global.isLive==0 and global.currentStopwatchRound==1{
    if global.myself.team==TEAM_RED{
        global.myAdcpTeam="Team A"
    }else if global.myself.team==TEAM_BLUE{
        global.myAdcpTeam="Team B"
    }
}
