arguments.argument:
var xoffset, yoffset, xsize, ysize, xshift, yshift;
xoffset = view_xview[0];
yoffset = view_yview[0];
xsize = view_wview[0];
ysize = view_hview[0];
xshift = -320 * global.timerPos;
yshift = 5 * global.timerPos;

draw_set_alpha(1);

draw_sprite_ext(sprite_index, 0, xoffset+xsize/2+1, yoffset+ysize+100, 3, 3, 0, c_white, 1)

draw_set_color(c_black)
draw_set_valign(fa_middle)
draw_set_halign(fa_center)

// Red kills
if (global.redCaps<=9){
    draw_text_transformed(xoffset+xsize/2-32, yoffset+ysize-40, string(global.redCaps), 2, 2, 0)
}else{
    draw_text(xoffset+xsize/2-32, yoffset+ysize-40, string(global.redCaps))
}

// Blue kills
if (global.blueCaps<=9){
    draw_text_transformed(xoffset+xsize/2+28, yoffset+ysize-40, string(global.blueCaps), 2, 2, 0)
}else{
    draw_text(xoffset+xsize/2+28, yoffset+ysize-40, string(global.blueCaps))
}

// Kill limit
if (killLimit<=9){
    draw_text_transformed(xoffset+xsize/2-2, yoffset+ysize-40, string(killLimit),2,2,0)
}else{
    draw_text(xoffset+xsize/2-2, yoffset+ysize-40, string(killLimit))
}

//timer
if instance_exists(WinBanner) exit;
draw_set_color(c_white);
draw_set_halign(fa_center);
draw_set_valign(fa_center);

if (global.myself != -1)
{
    if (global.myself.team == TEAM_RED)
        teamoffset = 0;
    else if (global.myself.team == TEAM_BLUE)
        teamoffset = 1;
}

draw_roundtimer(xoffset, yoffset, xsize, ysize, timer);
