arguments.argument:
if (instance_exists(target))
{
    xsize = view_wview[0];
    ysize = view_hview[0];
    xoffset = view_xview[0];
    yoffset = view_yview[0]-600+ysize;
    mid = view_wview[0]/2;
    HUDwidth = string_width("Healing: " + sanitiseNewlines(target.name))+20;
    nLength=string_width(sanitiseNewlines(target.name))

    draw_set_halign(fa_center);
    draw_set_color(c_white);
    draw_set_alpha(.5);
    if (target.object != -1) {
        if global.boxHUD==0{
            draw_sprite_stretched_ext(sprite_index, global.myself.team == TEAM_BLUE, xoffset+mid-(HUDwidth/2)+20, yoffset+450, HUDwidth, 36, c_white, 1);
            draw_text_color(xoffset+mid+20, yoffset+462, "Healing: " + sanitiseNewlines(target.name), c_white, c_white, c_white, c_white, 1);
            draw_healthbar(xoffset+mid-(HUDwidth/2)-24, yoffset+452,xoffset+mid-(HUDwidth/2)+20, yoffset+484,floor(target.object.hp*100/target.object.maxHp),c_black,c_white,c_white,3,true,false);
            draw_sprite_stretched_ext(HealedHudHPS, global.myself.team == TEAM_BLUE, xoffset+mid-(HUDwidth/2)-26, yoffset+450,48,36,c_white, 1);
            draw_sprite_stretched_ext(HealedHudCapS, global.myself.team == TEAM_BLUE, xoffset+mid+(HUDwidth/2)+18, yoffset+450,6,36,c_white, 1);
            draw_text_color(xoffset+mid-(HUDwidth/2)-2, yoffset+468, floor(target.object.hp), c_white, c_white, c_white, c_white, 1);
        }else{
            yoffset = view_yview[0]
            draw_set_color(c_black)
            draw_set_alpha(global.boxBgAlpha/100)
            draw_rectangle((xoffset+xsize/2)-35-(nLength/3),(yoffset+ysize/2)+145,(xoffset+xsize/2)+35+(nLength/3),(yoffset+ysize/2)+185,0)
            draw_set_alpha(global.boxElementAlpha/100)
            draw_set_color(make_color_rgb(50,200,50))

            draw_text_transformed((xoffset+xsize/2),(yoffset+ysize/2)+155,sanitiseNewlines(target.name),1,1,0)
            draw_text_transformed((xoffset+xsize/2),(yoffset+ysize/2)+168,floor(target.object.hp),1,1,0)
            draw_healthbar((xoffset+xsize/2)-32,(yoffset+ysize/2)+176,(xoffset+xsize/2)+32,(yoffset+ysize/2)+181,floor(target.object.hp*100/target.object.maxHp),c_black,c_red,c_green,0,1,0)
        }
    }
}
