arguments.argument:
if(global.myself.object == -1 or global.myself.class != CLASS_ENGINEER){
    instance_destroy();
    exit; 
}

xsize = view_wview[0];
ysize = view_hview[0];

if (global.myself.sentry){
    if global.boxHUD==0{
        drawHealth(15, ysize-53, global.myself.sentry.hp, global.myself.sentry.maxHp);
        draw_sprite_ext(sprite_index, image_index, view_xview[0]-25, view_yview[0]+ysize-75, 2, 2, 0, c_white, 1);
    }else{
        drawBoxHealth(23, ysize-57, global.myself.sentry.hp, global.myself.sentry.maxHp);
    }
}
