arguments.argument:
if(global.winners == TEAM_RED) {
    teamoffset = 0;
} else if(global.winners == TEAM_BLUE) {
    teamoffset = 1;
} else {
    teamoffset = 2;
}

if(global.myself.team != global.winners) {
    sound=FailureSnd;
    global.myself.humiliated = 1;
    global.statsLosses+=1
} else {
    sound = VictorySnd;
    global.statsWins+=1
}

with (Player) {
    if team == global.winners
        humiliated = 0;
    else {
        humiliated = 1;
    }
}

//DSM stuff
//Stats
global.statsGames+=1
addStats()
global.alreadyWroteStats=1

//Replays
if global.recordingType==1 and global.recordingEnabled and global.justEnabledRecording==0{
    endRecording()
    global.recordingEnabled=1
}

if(!instance_exists(ArenaHUD)) {
    AudioControlPlaySong(sound, false);
}
