arguments.argument:
if other.team != team{
    damageAuto(ownerPlayer, other.id, sentryHitDmg)

    if ownerPlayer==global.myself{
        var damageDone;
        damageDone = instance_create(other.x,other.y,DamageIndicator)
        damageDone.dmg = sentryHitDmg
        global.myself.stats[DAMAGE]+=sentryHitDmg
        global.myself.roundStats[DAMAGE]+=sentryHitDmg
        damageDone.owner = other.id
    }

    other.lastDamageDealer = ownerPlayer;
    other.lastDamageSource = weapon;
    instance_destroy()
}
