arguments.argument:
gunSetSolids();
if (!place_free(x, y)) 
{
    instance_destroy();
    gunUnsetSolids();
    exit;
}
gunUnsetSolids();

if(other.team != team)
{
    damageSentry(ownerPlayer, other.id, hitDamage);

    if ownerPlayer==global.myself{
        var damageDone;
        damageDone = instance_create(other.x,other.y,DamageIndicator)
        damageDone.dmg = hitDamage
        global.myself.stats[DAMAGE]+=hitDamage
        global.myself.roundStats[DAMAGE]+=hitDamage
        damageDone.owner = other.id
    }

    other.lastDamageDealer = ownerPlayer;
    other.lastDamageSource = weapon;
    instance_destroy();
}
