arguments.argument:
if (direction >= 90 and direction <= 270)
    image_xscale = -1;
else
    image_xscale = 1;

if (team == TEAM_RED){
    sprite_index = SpyRedBackstabTorsoS;
    if global.dumbledankSprites==1{
        sprite_index=SpyRedBackstabTorsoSM;
    }
}else{
    sprite_index = SpyBlueBackstabTorsoS;
    if global.dumbledankSprites==1{
        sprite_index=SpyBlueBackstabTorsoSM;
    }
}

if (image_index == 32)
{
    done = 1;
    image_speed = 0;
}

if(!done){
    if(alpha < 1){
        alpha += 0.1 * global.delta_factor;
    }else{
        alpha = 1;
    }
}else{
    if(alpha > 0){
        alpha -= ((0.02*decayFactor)*(2/alpha))*global.delta_factor //0.1 * global.delta_factor;
    }else{
        if decayFactor!=1{
            owner.currentWeapon.alarm[2]=4/global.delta_factor
        }
        instance_destroy();
    }
}
