arguments.argument:
// Insert a new line if shift is held
if (keyboard_check(vk_shift))
{
    currentText += chr(10);
    cursorIndex += 1;
}
else
{
    // Otherwise, send a command
    if (currentText != "")
    {
        // Don't add consecutive duplicate commands
        if (ds_list_find_value(global.consoleInputHistory, ds_list_size(global.consoleInputHistory) - 1) != currentText)
        {
            ds_list_add(global.consoleInputHistory, currentText);

            while (ds_list_size(global.consoleInputHistory) > global.consoleInputHistoryMax)
            {
                ds_list_delete(global.consoleInputHistory, 0);
            }
        }
        inputHistoryIndex = ds_list_size(global.consoleInputHistory);
        console_send_command(string_replace_all(currentText, "\#", "#"), -1);
    }

    currentText = "";
    suggestedText = "";
    cursorIndex = 0;
    cursorScrollDelay = 0;
    outputHistoryOffset = 0;
}
