arguments.argument:
// Insert a new line if shift is held
if (keyboard_check(vk_shift))
{
    currentText += chr(10);
    cursorIndex += 1;
}
else
{
    // Otherwise, send a command
    if (currentText != "")
    {
        ds_list_add(global.consoleInputHistory, currentText);
        while (ds_list_size(global.consoleInputHistory) > global.consoleInputHistoryMax)
        {
            ds_list_delete(global.consoleInputHistory, 0);
        }
        inputHistoryIndex = ds_list_size(global.consoleInputHistory);
        console_send_command(currentText, -1);
    }

    currentText = "";
    suggestedText = "";
    cursorIndex = 0;
    cursorScrollDelay = 0;
}
