arguments.argument:
// Set console dimensions and positions for drawing
// View Area
xoffset = view_xview[0];
yoffset = view_yview[0];
xsize = view_wview[0] + xoffset;
ysize = view_hview[0] + yoffset;

// Horizontal Bounds
boxLeft = xoffset + boxPaddingWidth;
boxRight = xsize - boxPaddingWidth;
global.consoleWidth = boxRight - boxLeft;

// Input Box
inputBoxBottom = ysize - boxPaddingHeight;
inputBoxTop = inputBoxBottom - 2 - (inputBoxPaddingHeight * 2) - (inputBoxRows * characterHeight);

// Output Box
outputBoxBottom = inputBoxTop - boxPaddingHeight;
outputBoxTop = outputBoxBottom - (displayedLines * lineSpacing) - boxPaddingHeight;

// Fullscreen Button
fullscreenXRight = boxRight;
fullscreenXLeft = fullscreenXRight - 23;

if (fullscreen)
{
    // Pin to top when fullscreen
    fullscreenYTop = yoffset + boxPaddingHeight;
    fullscreenYBottom = fullscreenYTop + 21;
}
else
{
    fullscreenYBottom = outputBoxTop - boxPaddingHeight;
    fullscreenYTop = fullscreenYBottom - 21;
}
