arguments.argument:
if (instance_exists(DeathCam) or message == "" or global.myself == -1)
    exit;

if (global.myself.alarm[5] > 0 and global.myself.team != TEAM_SPECTATOR)
{
    xoffset = view_xview[0];
    yoffset = view_yview[0];

    var padding, xbegin, ybegin, width, height;
    // TODO: Adjust with timer position, currently accounts for left aligned timer + stacked KOTH timers
    padding = 8;
    xbegin = xoffset + 36;
    ybegin = yoffset + 108;
    width = string_width(message) + (padding * 2);
    height = string_height(message) + (padding * 2);

    draw_set_alpha(0.5);
    draw_set_color(c_black);
    draw_rectangle(xbegin, ybegin, xbegin + width, ybegin + height, false);

    // Add border
    draw_set_alpha(1);
    draw_set_color(make_color_rgb(217, 217, 183));
    draw_rectangle(xbegin, ybegin, xbegin + width, ybegin + height, true);

    // Draw text
    draw_set_color(c_white);
    draw_set_halign(fa_left);
    draw_set_valign(fa_top);
    draw_text(xbegin + padding, ybegin + padding, message);
}
