arguments.argument:
if (room == Menu or room == Lobby) instance_destroy();

if (global.performanceOverlayPosition > 0 and global.showPing > 0 and !global.isHost)
{
    if (pingTimer > 0)
    {
        pingTimer -= 1 * global.delta_factor;
    }
    else if (!pingRunning)
    {
        pingSocket = tcp_connect(global.serverIP, global.serverPort);
        pingStartTime = current_time;
        pingRunning = true;
    }
    else if (!socket_connecting(pingSocket))
    {
        if (socket_has_error(pingSocket))
        {
            currentPing = 1000;
        }
        else
        {
            currentPing = current_time - pingStartTime;
        }

        socket_destroy_abortive(pingSocket);
        pingRunning = false;
        pingTimer = 30;

        if (global.showPing >= 2)
        {
            ds_list_add(pingHistory, currentPing);

            if (ds_list_size(pingHistory) > pingHistoryMax)
                ds_list_delete(pingHistory, 0);
        }

        if (currentPing >= 1000)
            currentPing = "X";
    }
}
