arguments.argument:
menu_create(128, 100, 500, 260, 32, 0, 96, 16);
menumode = true;

if (room != DSMOptionsRoom)
    menu_setdimmed();

menu_background(512, 24, 8, 12, 4);
bgtabs = true;

menu_addedit_num("Master Volume:", "global.masterVolume", '
    DSM_write_ini("Audio", "Master Volume", argument0);
    sound_global_volume(argument0 / 100);
', 100);


menu_addtablink("General", '
    instance_destroy();
    instance_create(x,y,DSMOptions);
');
menu_addtablink("Hosting", '
    instance_destroy();
    instance_create(x,y,DSMOptions_Hosting);
');
menu_addtablink("Console", '
    instance_destroy();
    instance_create(x,y,DSMOptions_Console);
');
menu_addtablink("Controls", '
    instance_destroy();
    instance_create(x,y,DSMOptions_Controls);
');

menu_addback("Back", '
    instance_destroy();
    if(room == DSMOptionsRoom)
        room_goto_fix(Menu);
    else
        instance_create(0,0,InGameMenuController);
');
