arguments.argument:
menu_create(144, 100, 500, 260, 32, 0, 112, 16);
menumode = true;

if (room != DSMOptionsRoom)
    menu_setdimmed();

menu_background(512, 24, 8, 12, 4);
bgtabs = true;

menu_divider("FPS & Ping Display");

menu_addedit_select("Display Position:", "global.performanceOverlayPosition", '
    DSM_write_ini("General", "Performance Overlay Position", argument0);
');
menu_add_option(0, "Off");
menu_add_option(1, "Top Left");
menu_add_option(2, "Top Right");
menu_add_option(3, "Bottom Left");
menu_add_option(4, "Bottom Right");

menu_addedit_boolean("Display Background:", "global.performanceOverlayBackground", '
    DSM_write_ini("General", "Performance Overlay Background", argument0);
');

menu_addedit_boolean("Show FPS:", "global.showFPS", '
    DSM_write_ini("General", "Show FPS", argument0);
');

menu_addedit_boolean("Show Ping:", "global.showPing", '
    DSM_write_ini("General", "Show Ping", argument0);
');

menu_addedit_select("Show Ping Graph:", "global.showPingGraph", '
    DSM_write_ini("General", "Show Ping Graph", argument0);
');
menu_add_option(0, "Off");
menu_add_option(1, "Yes (Always)");
menu_add_option(2, "Yes (On Scoreboard Open)");

menu_divider("Audio");

menu_addedit_num_percent("Master Volume:", "global.masterVolume", '
    DSM_write_ini("Audio", "Master Volume", argument0);
    sound_global_volume(argument0 / 100);
', 100);

menu_divider("Window Options");

menu_addedit_boolean("Borderless Window:", "global.borderlessWindow", '
    DSM_write_ini("General", "Borderless Window", argument0);
    window_set_showborder(!argument0);
    window_set_showicons(!argument0);
');

menu_addedit_boolean("Disable Window Resizing:", "global.disableWindowResizing", '
    DSM_write_ini("General", "Disable Window Resizing", argument0);
    window_set_sizeable(!argument0);
');

menu_divider("Miscellaneous");

menu_addedit_boolean("Skip FAUCeT Logo:", "global.skipFaucetLogo", '
    DSM_write_ini("General", "Skip FAUCeT Logo", argument0);
');

menu_addedit_boolean("Disable AFK Auto-Spectate:", "global.noAFKAutoSpectate", '
    DSM_write_ini("General", "Disable AFK Auto-Spectate", argument0);
');

DSM_menu_tabs();
