arguments.argument:
if (room != Menu and room != Options and room != Lobby)
    instance_destroy();

var spriteLeft, spriteRight, spriteTop, spriteBottom;
spriteLeft = x;
spriteRight = x + (sprite_width);
spriteTop = y;
spriteBottom = y + (sprite_height);

// Stop map going below top of view / above bottom of view
if ((spriteTop > -20 and vspeed > 0) or (spriteBottom < view_hview[0] + 20 and vspeed < 0))
{
    vspeed *= -1;
}

// Stop map going past left of view / right of view
if ((spriteLeft > -20 and hspeed > 0) or (spriteRight < view_wview[0] + 20 and hspeed < 0))
{
    hspeed *= -1;
}
