arguments.argument:
draw_set_color(c_white);
draw_set_alpha(1);

// Map sprite
draw_sprite_ext(sprite_index, image_index, round(x), round(y), image_xscale, image_yscale, image_angle, c_white, image_alpha);

if (!instance_exists(MainMenuController))
    exit;

// Game version
draw_sprite(MenuBackgroundVersionS, 0, view_wview[0], view_hview[0]);

// DSM version text
draw_set_font(global.menuFontDSM);
draw_set_halign(fa_left);
draw_set_valign(fa_bottom);

// Re-DSM Logo
draw_sprite_ext(MenuLogoDSMS, 0, 32, view_hview[0] - 84, 4, 4, 0, c_white, 1);

// Re-DSM Version
var stringText, offset;
stringText = DSM_VERSION_STRING;
offset = 8;

draw_set_color(make_color_rgb(64, 64, 64));
draw_text_transformed(176 + offset, view_hview[0] - 12 + offset, stringText, 8, 8, 0);

draw_set_color(make_color_rgb(198, 152, 66));
draw_text_transformed(176, view_hview[0] - 12, stringText, 8, 8, 0);

draw_set_font(global.gg2Font);
