// Set a random start position within the bounds of the screen
var xMin, yMin, xMax, yMax;
xMin = 50;
yMin = 50;
xMax = sprite_width - view_wview[0] - 50;
yMax = sprite_height - view_hview[0] - 50;

x = random_range(xMin, xMax) * -1;
y = random_range(yMin, yMax) * -1;

// Set a random movement speed and direction
hspeed = random_range(xSpeedMin, xSpeedMax) * choose(-global.delta_factor, global.delta_factor);
vspeed = random_range(ySpeedMin, ySpeedMax) * choose(-global.delta_factor, global.delta_factor);
