arguments.argument:
if (ammoCount < 0){
    ammoCount = 0;
}else if (ammoCount <= maxAmmo and isRefilling){
    //ammo sputtering
    if ammoCount<=ammoCost{
        ammoCount+=(ammoCost*2) * global.delta_factor;
    }else{
        ammoCount += ammoCost * global.delta_factor;
    }
}

if ammoCount>maxAmmo{
    ammoCount=maxAmmo
}
